import styled from 'styled-components';

export const Wrapper = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  margin: 0px;
  padding: 0px;
  height: 100vh;
`;

export const Title = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 1.5rem;
  font-weight: 600;
`;

export const Form = styled.div`
  background-color: #f1f3f5;
  border: 1px solid #ced4da;
  border-radius: 10px;
  width: 70%;
  height: 70%;
`;

export const Inputs = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  flex-direction: column;
  gap: 10px;
  height: 100%;
`;

export const Input = styled.input`
  font-size: 1.3rem;
  border-radius: 10px;
  border: 1px solid #ced4da;
  padding: 8px 3%;
  width: 40vw;
  &::placeholder {
    color: #dee2e6;
  }
  &:focus {
    outline: none;
    color: #868e98;
    font-family: 'Montserrat';
  }
`;
