import React from 'react';
import Navbar from '../components/Navbar/Navbar.js';
import { styled } from 'styled-components';

const Home = () => {
  return (
    <Wrapper>
      <Navbar />
      <Title>로그인</Title>
      <Form>
        <Inputs>
          <Input placeholder='ID' />
          <Input placeholder='PASSWORD' />
        </Inputs>
        <Button>Login</Button>
      </Form>
    </Wrapper>
  );
};

export const Wrapper = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  flex-direction: column;
`;

export const Title = styled.div`
  display: flex;
  position: relative;
  font-size: 1.5rem;
  font-weight: 600;
  margin-top: 2rem;
  margin-bottom: 20px;
  color: #495057;
`;

export const Form = styled.div`
  display: flex;
  background-color: #f1f3f5;
  border: 1px solid #ced4da;
  border-radius: 10px;
  padding: 3rem 3.5rem;
`;

export const Inputs = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  flex-direction: column;
  gap: 10px;
  height: 100%;
`;

export const Input = styled.input`
font-size: 0.875rem;
border-radius: 10px;
border: 1px solid #ced4da;
padding: 8px 0.5rem;
width: 
&::placeholder {
  color: #dee2e6;
  }
  &:focus {
    outline: none;
    color: #868e98;
    font-family: 'Montserrat';
    }
    `;

const Button = styled.button`
  display: flex;
  justify-content: center;
  align-items: center;
  color: white;
  padding: 1.5rem;
  border-radius: 10px;
  margin-left: 20px;
  color: #868e98;
  border: 1px solid #ced4da;
  background-color: #f8f9fa;
`;

export default Home;
