import { logRoles } from '@testing-library/dom';
import axios from 'axios';

export const instance = axios.create({
  baseURL: 'http://localhost:8080',
  headers: {
    'Content-Type': 'application/json',
  },
  withCredentials: true,
});

instance.interceptors.request.use(
  (config) => {
    const accessToken = localStorage.getItem('accessToken');
    const refreshToken = localStorage.getItem('refrechToken');

    if (accessToken) {
      config.headers['Authorization'] = `Bearer ${accessToken}`;
    }

    if (refreshToken) {
      config.headers['x-refesh-token'] = refreshToken;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

instance.interceptors.response.use(
  async (response) => {
    return response;
  },
  async (error) => {
    const {
      config,
      response: { status, data },
    } = error;

    if (status === 401 && data.message === 'InvalidTokenException') {
      logout();
    }

    if (status === 401 && data.message === 'TokenExpired') {
      try {
        const tokenRefreshResult = await instance.post('/refresh-token');
        if (tokenRefreshResult.status === 200) {
          const { accessToken, refreshToken } = tokenRefreshResult.data;
          localStorage.setItem('accessToken', accessToken);
          localStorage.setItem('refreshToken', refreshToken);
          return instance(config);
        } else {
          logout();
        }
      } catch (e) {
        logout();
      }
    }
    return Promise.reject(error);
  }
);

export const SignUp = async (email, password, name) => {
  const result = await instance.post('/signup', {
    headers: {
      ...instance.headers,
    },
    data: {
      email,
      password,
      name,
    },
  });
  return result;
};

export const login = async (email, password) => {
  const result = await instance.post('/login', {
    data: {
      email,
      password,
    },
  });
  console.log(result);
  return result;
};

export const logout = () => {};
