import styled from 'styled-components';

export const Wrapper = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  margin: 0px;
  padding: 0px;
  height: 100vh;
`;

export const Form = styled.div`
  background-color: #f1f3f5;
  border: 1px solid
  border-radius: 10px;
  width: 70%;
  height: 70%;
`;

export const Inputs = styled.div`
  display: flex;
  align-items: center;
  flex-direction: column;
`;

export const Input = styled.input`
  font-size: 20px;
  height: 30px;
  border-radius: 10px;
  border: none;
  padding: 10px;
  &::placeholder {
    color: #;
    font: 500 20px 'ChosunGu';
  }
`;
